import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../context/UserContext';
import SessionCard from '../components/SessionCard';
import './DashboardPage.css';

const DashboardPage = () => {
  const [activeTab, setActiveTab] = useState('join');
  const [sessions, setSessions] = useState([]);
  const [sessionCode, setSessionCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  
  const { user } = useContext(UserContext);
  const navigate = useNavigate();
  
  // ÿ¨ŸÑÿ® ÿ¨ŸÑÿ≥ÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
  const fetchUserSessions = async () => {
    try {
      setLoading(true);
      
      // ÿ™ŸÉŸàŸäŸÜ ÿßŸÑŸáŸäÿØÿ±
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      };
      
      // ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const { data } = await axios.get('/api/sessions/mysessions', config);
      
      setSessions(data.sessions);
      setLoading(false);
    } catch (error) {
      setErrorMessage('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ¨ŸÑÿ® ÿßŸÑÿ¨ŸÑÿ≥ÿßÿ™');
      setLoading(false);
    }
  };
  
  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¨ŸÑÿ≥ÿßÿ™ ÿπŸÜÿØ ŸÅÿ™ÿ≠ ÿßŸÑÿ™ÿ®ŸàŸäÿ®
  useEffect(() => {
    if (activeTab === 'sessions') {
      fetchUserSessions();
    }
  }, [activeTab]);
  
  // ÿßŸÑÿ™ÿ®ÿØŸäŸÑ ÿ®ŸäŸÜ ÿßŸÑÿ™ÿ®ŸàŸäÿ®ÿßÿ™
  const switchTab = (tab) => {
    setActiveTab(tab);
    setErrorMessage('');
    setSuccessMessage('');
  };
  
  // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ™ÿ∫ŸäŸäÿ± ŸÉŸàÿØ ÿßŸÑÿ¨ŸÑÿ≥ÿ©
  const handleCodeChange = (e) => {
    // ÿ™ÿ≠ŸàŸäŸÑ ÿßŸÑÿ≠ÿ±ŸàŸÅ ÿ•ŸÑŸâ ŸÉÿ®Ÿäÿ±ÿ© Ÿàÿ•ÿ≤ÿßŸÑÿ© ÿßŸÑŸÖÿ≥ÿßŸÅÿßÿ™
    const code = e.target.value.toUpperCase().replace(/\s/g, '');
    setSessionCode(code);
    setErrorMessage('');
    setSuccessMessage('');
  };
  
  // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ ÿ•ŸÑŸâ ÿ¨ŸÑÿ≥ÿ©
  const handleJoinSession = async (e) => {
    e.preventDefault();
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ•ÿØÿÆÿßŸÑ ÿßŸÑŸÉŸàÿØ
    if (!sessionCode || sessionCode.length !== 6) {
      setErrorMessage('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ŸÉŸàÿØ ÿµÿßŸÑÿ≠ ŸÖŸÉŸàŸÜ ŸÖŸÜ 6 ÿ£ÿ≠ÿ±ŸÅ');
      return;
    }
    
    try {
      setLoading(true);
      
      // ÿ™ŸÉŸàŸäŸÜ ÿßŸÑŸáŸäÿØÿ±
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      };
      
      // ÿ•ÿ±ÿ≥ÿßŸÑ ÿ∑ŸÑÿ® ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ
      const { data } = await axios.post('/api/sessions/join', { code: sessionCode }, config);
      
      setSuccessMessage('ÿ™ŸÖ ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ ÿ•ŸÑŸâ ÿßŸÑÿ¨ŸÑÿ≥ÿ© ÿ®ŸÜÿ¨ÿßÿ≠');
      setLoading(false);
      
      // ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿßŸÑÿ¨ŸÑÿ≥ÿ© ÿ®ÿπÿØ ÿ´ÿßŸÜŸäÿ©
      setTimeout(() => {
        navigate(`/session/${data.session._id}`);
      }, 1000);
    } catch (error) {
      setErrorMessage(error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ ÿ•ŸÑŸâ ÿßŸÑÿ¨ŸÑÿ≥ÿ©');
      setLoading(false);
    }
  };
  
  return (
    <div className="dashboard-page">
      <div className="dashboard-header">
        <h1 className="dashboard-title">ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ</h1>
      </div>
      
      <div className="dashboard-tabs">
        <div 
          className={`dashboard-tab ${activeTab === 'join' ? 'active' : ''}`}
          onClick={() => switchTab('join')}
        >
          ÿßŸÜÿ∂ŸÖ/ÿ£ŸÜÿ¥ÿ¶ ŸÑÿπÿ®ÿ©
        </div>
        <div 
          className={`dashboard-tab ${activeTab === 'sessions' ? 'active' : ''}`}
          onClick={() => switchTab('sessions')}
        >
          ÿ¨ŸÑÿ≥ÿßÿ™Ÿä
        </div>
      </div>
      
      <div className="dashboard-content">
        {activeTab === 'join' ? (
          <div className="join-content">
            <div className="card">
              <h2 className="section-title text-center">üîπ ÿ¨ŸÑÿ≥ÿßÿ™ ÿßŸÑÿ™ŸàŸÇÿπÿßÿ™ üîπ</h2>
              <p className="section-description text-center">
                ÿ¥ÿßÿ±ŸÉ ŸÅŸä ÿ¨ŸÑÿ≥ÿßÿ™ ÿ£Ÿà ÿßÿ≥ÿ™ÿπÿ±ÿ∂ ÿ¨ŸÑÿ≥ÿßÿ™ŸÉ
              </p>
              
              <div className="join-section">
                <h3 className="subsection-title text-center">üîπ ŸÉŸàÿØ ÿßŸÑŸÑÿπÿ®ÿ© üîπ</h3>
                
                {errorMessage && (
                  <div className="alert alert-error">{errorMessage}</div>
                )}
                
                {successMessage && (
                  <div className="alert alert-success">{successMessage}</div>
                )}
                
                <form onSubmit={handleJoinSession}>
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-control session-code-input"
                      placeholder="ÿ£ÿØÿÆŸÑ ŸÉŸàÿØ ÿßŸÑŸÑÿπÿ®ÿ© ÿßŸÑŸÖŸÉŸàŸÜ ŸÖŸÜ 6 ÿ£ÿ≠ÿ±ŸÅ"
                      value={sessionCode}
                      onChange={handleCodeChange}
                      maxLength={6}
                    />
                  </div>
                  
                  <button 
                    type="submit" 
                    className="btn btn-primary btn-block mb-4"
                    disabled={loading}
                  >
