import React from 'react';
import './PredictionItem.css';

// مصفوفة ألوان واضحة ومتباينة للأفاتارات
const AVATAR_COLORS = [
  '#5e60ce', // أرجواني
  '#48bfe3', // أزرق فاتح
  '#ec4899', // وردي
  '#06d6a0', // نعناعي
  '#ffd166', // أصفر
  '#ef476f', // أحمر وردي
  '#118ab2', // أزرق متوسط
  '#073b4c', // أزرق داكن
  '#ff9f1c', // برتقالي
  '#7209b7', // بنفسجي
  '#f72585', // وردي ساطع
  '#3a0ca3', // أزرق بنفسجي
  '#4cc9f0', // سماوي
  '#6a994e', // أخضر
  '#bc6c25', // بني
  '#22577a', // أزرق مخضر
  '#d00000', // أحمر
  '#8d99ae', // رمادي مزرق
  '#2ec4b6', // فيروزي
  '#9d4edd'  // أرجواني متوسط
];

const PredictionItem = ({ prediction, isCurrentUser }) => {
  // تنسيق التاريخ والوقت
  const formatDateTime = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString('ar-SA', options);
  };

  // توليد حرف أولي للمستخدم
  const getInitial = (username) => {
    return username.charAt(0).toUpperCase();
  };

  // توليد لون ثابت للمستخدم بناءً على اسم المستخدم
  const getAvatarColor = (username) => {
    // توليد رقم فريد من اسم المستخدم
    let hash = 0;
    for (let i = 0; i < username.length; i++) {
      hash = username.charCodeAt(i) + ((hash << 5) - hash);
    }
    
    // استخدام القيمة المطلقة للحصول على رقم موجب دائمًا
    const index = Math.abs(hash) % AVATAR_COLORS.length;
    
    // استخدام الرقم لاختيار لون من المصفوفة
    return AVATAR_COLORS[index];
  };

  return (
    <div className={`prediction-item ${isCurrentUser ? 'current-user' : ''}`}>
      <div className="prediction-header">
        <div className="user-info">
          <div 
            className="avatar" 
            style={{ backgroundColor: getAvatarColor(prediction.user.username) }}
          >
            {getInitial(prediction.user.username)}
          </div>
          <div className="user-details">
            <div className="username">
              {prediction.user.username}
              {isCurrentUser && <span className="user-badge">أنت</span>}
            </div>
            <div className="prediction-time">{formatDateTime(prediction.createdAt)}</div>
          </div>
        </div>
      </div>
      
      <div className="prediction-content">
        <p>{prediction.text}</p>
      </div>
    </div>
  );
};

export default PredictionItem;
